import React from "react";

const QuestionWindow = ({ leetcodeQuestion }) => {
  return (
    <div 
      className="w-full h-64 rounded-md text-black font-normal text-lg overflow-y-auto p-4 text-justify"
      style={{ fontFamily: "'Segoe UI', 'Tahoma', 'Geneva', 'Verdana', 'sans-serif'" }}>
      {leetcodeQuestion ? <p>{leetcodeQuestion}</p> : null}
    </div>
  );
};

export default QuestionWindow;
