import React, { useEffect, useState, useCallback } from "react";
import CodeEditorWindow from "./CodeEditorWindow";
import axios from "axios";
import { classnames } from "../utils/general";
import { languageOptions } from "../constants/languageOptions";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { defineTheme } from "../lib/defineTheme";
import useKeyPress from "../hooks/useKeyPress";
import OutputWindow from "./OutputWindow";
import QuestionWindow from "./QuestionWindow";
import ThemeDropdown from "./ThemeDropdown";
import LanguagesDropdown from "./LanguagesDropdown";
import CustomWebcam from "./CustomWebcam";

const silenceThreshold = -60; // in dB, threshold for considering silence
const silenceTime = 5000; // time in ms to wait for silence before stopping the recording
let silenceStart = null;
let audioChunks = [];
let mediaRecorder;

// Start recording from the microphone
async function startRecording() {
    try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const audioContext = new AudioContext();
        const source = audioContext.createMediaStreamSource(stream);
        const processor = audioContext.createScriptProcessor(4096, 1, 1);

        source.connect(processor);
        processor.connect(audioContext.destination);

        mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.start();
        mediaRecorder.ondataavailable = event => audioChunks.push(event.data);

        processor.onaudioprocess = event => {
            const input = event.inputBuffer.getChannelData(0);
            let sum = 0.0;

            for (let i = 0; i < input.length; ++i) {
                sum += input[i] * input[i];
            }

            const instantVolume = Math.log10(sum / input.length) * 10;
            checkSilence(instantVolume);
        };
    } catch (error) {
        console.error('Error starting audio recording:', error);
    }
}

// Check for silence
function checkSilence(volume) {
    if (volume < silenceThreshold) {
        if (!silenceStart) {
            silenceStart = new Date().getTime();
        } else {
            const elapsed = new Date().getTime() - silenceStart;
            if (elapsed > silenceTime) {
                stopRecording();
                silenceStart = null;
            }
        }
    } else {
        silenceStart = null;
    }
}

// Stop the recording and process the audio
function stopRecording() {
    mediaRecorder.stop();
    mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
        sendAudioToBackend(audioBlob);
        audioChunks = [];
    };
}

// Send the audio blob to the backend
function sendAudioToBackend(audioBlob) {
    const formData = new FormData();
    formData.append("audio", audioBlob);

    fetch("http://your-backend-url.com/audio-endpoint", {
        method: "POST",
        body: formData
    })
    .then(response => response.json())
    .then(data => console.log("Response from backend:", data))
    .catch(error => console.error("Error sending audio to backend:", error));
}

// Start the recording process
startRecording();


const javascriptDefault = `//Welcome to Code Editor!`;

const leetcodeQuestion = `Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.\ 
You may assume that each input would have exactly one solution, and you may not use the same element twice.
You can return the answer in any order.`;

const Landing = () => {

  const [code, setCode] = useState(javascriptDefault);
  const [customInput, setCustomInput] = useState("");
  const [outputDetails, setOutputDetails] = useState(null);
  const [processing, setProcessing] = useState(null);
  const [running, setRunning] = useState(null);
  const [theme, setTheme] = useState("cobalt");
  const [language, setLanguage] = useState(languageOptions[0]);

  // const leetcodeQuestion = `Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target. You may assume that each input would have exactly one solution, and you may not use the same element twice. You can return the answer in any order.`;

  const enterPress = useKeyPress("Enter");
  const ctrlPress = useKeyPress("Control");

  const onSelectChange = (sl) => {
    console.log("selected Option...", sl);
    setLanguage(sl);
  };

  const showSuccessToast = useCallback((msg) => {
    toast.success(msg || `Compiled Successfully!`, {
      position: "top-right",
      autoClose: 1000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  }, []); // No dependencies, so an empty array

  const showErrorToast = useCallback((msg, timer) => {
    toast.error(msg || `Something went wrong! Please try again.`, {
      position: "top-right",
      autoClose: timer ? timer : 1000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  }, []); // No dependencies, so an empty array


  const checkStatus = useCallback(async (token) => {
    const options = {
      method: 'GET',
      url: `${process.env.REACT_APP_RAPID_API_URL}/${token}`,
      params: { base64_encoded: 'true', fields: '*' },
      headers: {
        'X-RapidAPI-Host': process.env.REACT_APP_RAPID_API_HOST,
        'X-RapidAPI-Key': process.env.REACT_APP_RAPID_API_KEY,
      },
    };

    try {
      let response = await axios.request(options);
      let statusId = response.data.status?.id;

      // Check if the response has a status indicating success
      if (statusId === 3) { // Replace '3' with the actual success status ID
        setProcessing(false);
        setOutputDetails(response.data);
        showSuccessToast(`Compiled Successfully!`);
      } else {
        // If status is not 'success', show an error toast
        setProcessing(false);
        setOutputDetails(response.data.error);
        showErrorToast(response.data.error || `Compilation failed!`);
      }
    } catch (err) {
      setProcessing(false);
      showErrorToast(`Something went wrong! Please try again.`);
    }
  }, [setProcessing, setOutputDetails, showSuccessToast, showErrorToast]);


  const handleCompile = useCallback(() => {
    console.log("handleCompile called...");
    setProcessing(true);
    const formData = {
      language_id: language.id,
      source_code: btoa(code),
      stdin: btoa(customInput),
    };
    const options = {
      method: "POST",
      url: process.env.REACT_APP_RAPID_API_URL,
      params: { base64_encoded: "true", fields: "*" },
      headers: {
        "content-type": "application/json",
        "Content-Type": "application/json",
        "X-RapidAPI-Host": process.env.REACT_APP_RAPID_API_HOST,
        "X-RapidAPI-Key": process.env.REACT_APP_RAPID_API_KEY,
      },
      data: formData,
    };

    axios.request(options)
      .then(function (response) {
        const token = response.data.token;
        checkStatus(token); // Ensure checkStatus is stable or included in dependencies
      })
      .catch((err) => {
        let error = err.response ? err.response.data : err;
        let status = err.response ? err.response.status : null;
        console.log("Error status", status);
        if (status === 429) {
          showErrorToast(`Quota of 100 requests exceeded for the Day!`, 10000);
        }
        setProcessing(false);
        console.log("Error in handleCompile: ", error);
      });
  }, [code, customInput, language, setProcessing, checkStatus, showErrorToast]);

  const handleStartEnd = () => {
    setRunning(!running);
  };

  useEffect(() => {
    if (enterPress && ctrlPress) {
      console.log("enterPress", enterPress);
      console.log("ctrlPress", ctrlPress);
      handleCompile();
    }
  }, [ctrlPress, enterPress, handleCompile]);
  const onChange = (action, data) => {
    switch (action) {
      case "code": {
        setCode(data);
        break;
      }
      default: {
        console.warn("case not handled!", action, data);
      }
    }
  };

  function handleThemeChange(th) {
    const theme = th;
    console.log("theme...", theme);

    if (["light", "vs-dark"].includes(theme.value)) {
      setTheme(theme);
    } else {
      defineTheme(theme.value).then((_) => setTheme(theme));
    }
  }
  useEffect(() => {
    defineTheme("oceanic-next").then((_) =>
      setTheme({ value: "oceanic-next", label: "Oceanic Next" })
    );
  }, []);

  return (
    <>
      <ToastContainer
        position="top-right"
        autoClose={2000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      <div className="h-4 w-full"></div>
      <div className="flex flex-row space-x-4 items-start px-4 py-4">
        <div className="left-container flex w-1/3 flex-col h-screen">
          <div className="h-1/2 mr-5 pt-20">
            <div className="w-full h-full"><QuestionWindow leetcodeQuestion={running ? leetcodeQuestion : ""} /></div>
          </div>
          <div className="h-1/2 mr-5 mb-2">
            <CustomWebcam />
          </div>
        </div>
        <div className="right-container flex flex-col w-2/3 h-screen">
          <div className="flex flex-row w-full justify-end items-end px-4 py-2 mb-4 space-x-4">
            <button
              onClick={handleStartEnd}
              className={
                `text-white border-2 border-black z-10 rounded-md shadow-[5px_5px_0px_0px_rgba(0,0,0)] px-8 py-2.5 hover:shadow transition duration-200 flex-shrink-0 mr-auto ${!code ? "opacity-50" :
                  running ? "bg-red-500 hover:bg-red-600" : "bg-green-500 hover:bg-green-600"
                }`
              }
            >
              {running ? "Stop" : "Start"}
            </button>
            <LanguagesDropdown onSelectChange={onSelectChange} />
            <ThemeDropdown handleThemeChange={handleThemeChange} theme={theme} />
            <button
              onClick={handleCompile}
              disabled={!code}
              className={classnames(
                "border-2 border-black z-10 rounded-md shadow-[5px_5px_0px_0px_rgba(0,0,0)] px-4 py-2.5 hover:shadow transition duration-200 bg-white flex-shrink-0",
                !code ? "opacity-50" : ""
              )}
            >
              {processing ? "Processing..." : "Compile and Execute"}
            </button>
          </div>
          <div className="h-3/5 mr-5 mb-2">
            <CodeEditorWindow
              code={code}
              onChange={onChange}
              language={language?.value}
              theme={theme.value}
            />
          </div>
          <div className="flex flex-row w-full justify-start items-start px-4">
            <h1 className="font-bold text-xl bg-clip-text text-transparent bg-gradient-to-r from-slate-900 to-slate-700 mb-2">
              Output
            </h1>
          </div>
          <div className="h-2/5 mr-5 mb-2">
            <div className="w-full"><OutputWindow outputDetails={outputDetails} /></div>
            {/* <div className="w-1/3">{outputDetails && <OutputDetails outputDetails={outputDetails} />}</div> */}
          </div>
        </div>
      </div>
    </>
  );
};
export default Landing;
